import react from 'react';
import {useParams} from 'react-router-dom'
import {useEffect} from 'react'
import {useState} from 'react'
import axios from 'axios'
import './css/App.css'




function Post(){
  const {id}=useParams();
  const [dataa,setdata]=useState({});
  const [Comments,setComments]=useState([])




useEffect(()=>{
  axios.get(`http://localhost:3001/posts/${id}`).then((result)=>{

    setdata(result.data)
  })

  axios.get(`http://localhost:3001/Comments/${id}`).then((result)=>{
      setComments(result.target.value)

  })
},[])

    return(
    <div className='App'>
      <div className='post'>
          <div className='title'>{dataa.title}</div>
          <div className='body'>{dataa.description}</div>
          <div className='username'>{dataa.username}</div>
      </div>
      <div>
          <div className='addComment'><input autoComplete='off' type={'text'} name='comment' placeholder={'Your comment'}></input> <button>Add</button></div>
          <div className='Comments'>{Comments.map((event)=>{
            return(
             <div className=''>{event.comment}</div>)
          })} </div>
      </div>
    </div>

)

}


export default Post;
