import react from 'react';
import {useParams} from 'react-router-dom'
import {useEffect} from 'react'
import axios from 'axios'




function Post(){
    const [ress,setres]=useState([]);
    let {id}=useParams();

    useEffect=()=>{
        axios.get(` http://localhost:3001/posts/${id}`).then((result)=>{
           setres(result.data) ;
        })
    }
    return(
        <div>{id}</div>
    )


}


export default Post;
