import react from 'react';
import {useParams} from 'react-router-dom'
import {useEffect} from 'react'
import {useState} from 'react'
import axios from 'axios'
import './css/App.css'




function Post(){
  const {id}=useParams();
  const [dataa,setdata]=useState({});
  




useEffect(()=>{
  axios.get(`http://localhost:3001/posts/${id}`).then((result)=>{

    setdata(result.data)
  })

  axios.post('/Comments',{})
},[])

    return(
    <div className='App'>
      <div className='post'>
          <div className='title'>{dataa.title}</div>
          <div className='body'>{dataa.description}</div>
          <div className='username'>{dataa.username}</div>
      </div>
      <div>
          <div className='addComment'><input type={'text'} name='comment' placeholder={'Your comment'}></input> <button>Add</button></div>
          <div className='Comments'> </div>
      </div>
    </div>

)

}


export default Post;
