import React from 'react';
import {Formik,Form,Field,ErrorMessage} from 'formik';
import * as yup from 'yup';
import './css/input.css';
import axios from "axios";

function insert(){

    const initialValue={
        title:'',
        description:'',
        username:''
    }

    onSubmit=(data)=>{
        console.log(data)

    }
    const validationSchema=yup.object().shape({
        title : yup.string().required(),
        description :yup.string().min(5).max(90).required(),
        username : yup.string().min(5).max(10).required()
    })

    return(
        <div className='createPostPage'>

            <Formik initialValues={initialValue} onSubmit={onSubmit} validationSchema={validationSchema}>
                <Form className='formContainer'>
                    <label>Title :</label>
                    {/* <ErrorMessage name='title' component={label}/> */}
                    <Field id='create' name='title' placeholder='(Ex.Title)'/>
                    <label>Desciption:</label>
                    {/* <ErrorMessage name='description' component={label}/> */}
                    <Field id='create' name='description' placeholder='(Ex.Desciption)'/>
                    <label>username :</label>
                    {/* <ErrorMessage name='username' component={label}/> */}
                    <Field id='create' name='username' placeholder='(Ex.username)'/>

                    <button type='submit'>Submit</button>

                </Form>
            </Formik>
        </div>
    )

}


export default insert
