import react from 'react';
import {useParams} from 'react-router-dom'
import {useEffect} from 'react'
import {useState} from 'react'
import axios from 'axios'
import './css/App.css'




function Post(){
  const {id}=useParams();
  const [dataa,setdata]=useState({});
  const [Comments,setComments]=useState([])
  const [newComment,setnewComment]=useState('');



useEffect(()=>{
  axios.get(`http://localhost:3001/posts/${id}`).then((result)=>{

    setdata(result.data)
  })

  axios.get(`http://localhost:3001/Comments/${id}`).then((result)=>{
      setComments(result.data)

  })

let addComment=()=>{
    axios.post(`http://localhost:3001/Comments`,{comment:newComment,TableId:id}).then((result)=>{
    alert('comment added...')
  })

  }
},[])

    return(
    <div className='App'>
      <div className='post'>
          <div className='title'>{dataa.title}</div>
          <div className='body'>{dataa.description}</div>
          <div className='username'>{dataa.username}</div>
      </div>
      <div>
          <div className='addComment'><input onChange={(event)=>{setnewComment(event.target.value)}} autoComplete='off' type='text' placeholder={'Your comment'}/>
                                      <button onClick={addComment}>Add</button></div>
          <div className='Comments'>
                    {Comments.map((event,key)=>{
                            return  <div  key={key} className='Comments'>{event.comment}</div>
                        
                        })
                    }   
          </div>
      </div>
    </div>

)

}


export default Post;
