import react from 'react';
import {useParams} from 'react-router-dom'
import {useEffect} from 'react'
import axios from 'axios'




function Post(){
    const [ress,setres]=useState([]);
    let {id}=useParams();

    useEffect=()=>{
        axios.get(` http://localhost:3001/posts/${id}`).then((result)=>{
           setres(result.data) ;
        })
    }
    return(
      
        <div className="App">
          
          {ress.map((value=> {
            return(
              <div className="post">       
                  <div className='title'><label></label> {value.title}</div>
                  <div className='body'><label></label> {value.description}</div>
                  <div className='footer'><label></label>{value.username}</div>
              </div>
            )

            })
          )}
        
    </div>
    
)

}


export default Post;
