import React,{Component} from "react";
import {useState,useEffect} from 'react'
import Axios from 'axios'
import './App.css'



function App(){

  const [getdata,setgetData]=useState([])
  useEffect(()=>{
    Axios.get('http://localhost:3001/posts').then((result)=>{setgetData(result.data)})
  },[])
        return(
            <div className="App">
              
                  {getdata.map((value=> {
                    return(
                      <div className="post">
                          <div className='id'><label>ID :</label> {value.id}</div>
                          <div className='title'><label>TITLE :</label> {value.title}</div>
                          <div className='body'><label>DESCTIPTION :</label> {value.description}</div>
                          <div className='footer'><label>USERNAME :</label>{value.username}</div>
                      </div>
                    )

                    })
                  )}
                
            </div>
        )

}


// export default App;
export default App;
