import React from 'react'
import Axios from 'axios'
import {useState,useEffect} from 'react'
import '../../css/App.css'

function Home(){

    const [getdata,setgetData]=useState([])


    useEffect(()=>{
      Axios.get('http://localhost:3001/posts').then((result)=>{setgetData(result.data)})
    },[])

    return(
     
            <div className="App">
              
              {getdata.map((value=> {
                return(
                  <div className="post">       
                      <div className='title'><label></label> {value.title}</div>
                      <div className='body'><label></label> {value.description}</div>
                      <div className='footer'><label></label>{value.username}</div>
                  </div>
                )

                })
              )}
            
        </div>
        
    )
}


export default Home
